  const dir = d => node.properties
                   && node.properties.meta 
                   && node.properties.meta.directives
                   && (node.properties.meta.directives.indexOf(d) == -1)
                   //&& d
  const info = dir('info') || dir('information')
  const warn = dir('warn') || dir('warning')
  const error = dir('err') || dir('error')
  const note = dir('box') || dir('note')

  const classes = [
     info && 'info',
     warn && 'warn',
     error && 'error',
     note && 'note',
     ''
  ]
  .filter(x=>x)
  .join(' ')
  .trim()

  const view = React.createElement('div', {className: "none"}, children)
  return classes || "still nothing"
}

