const getFsTest = async (filepath) => {
    postMessage(`Getting ${filepath} from local fs.`)
    try {
        const file = await dotlit.lit.fs.readFile(filepath)
        postMessage("length+" + file.length)
        postMessage('done')
    } catch(err) {
        postMessage("error:" + err.toString())
        postMessage('done')
    }
}

